/**
 *
 * @autor Christian Felipe Martinez Castaño
 * 2023
 *
 */
 import { Component, OnInit } from "@angular/core";
 import { Router, ActivatedRoute, Params } from "@angular/router";
 import { UserService } from "src/app/services/user.service";
 import { ProductsService } from "src/app/services/products.services";
 import { Products } from "src/app/models/products";


@Component({
  selector: 'app-usuario-productos',
  templateUrl: './usuario-productos.component.html',
  styleUrls: ['./usuario-productos.component.scss'],
  providers: [UserService, ProductsService]
})
export class UsuarioProductosComponent {

  public title: string;
  public token: any;
  public products: Array<Products>;

  constructor(
    private _route: ActivatedRoute,
    private _router: Router,
    private _userService: UserService,
    private _productsService: ProductsService
  ){
    this.title = 'Lista de productos';
    this.token = this._userService.getToken();
    this.products = [];
  }

  ngOnInit(): void {
    this.getProduct();
  }

  getProduct(){

    this._route.params.subscribe(params => {

      let id = + params['id'];
      this._productsService.getProduct(this.token, id).subscribe(

        response =>{

          if(response.status == 'success'){
            this.products = response.products;
          }else{
            this._router.navigate(['inicio']);
          }

        },
        error => {
          console.log(<any>error);
        }
       )
    })

  }

}
<div class="col-md-12" *ngIf="products">

  <h1>{{products.title}}</h1>

  <p>
    <strong *ngIf="products.status == 'true' ">Este producto se encuentra disponible</strong>
    <strong *ngIf="products.status == 'false' ">Este producto no se encuentra disponible</strong>
  </p>
  <p>
    $ {{products.price}}
  </p>
  <p>
    {{products.description}}
  </p>

</div>
