import { Component, OnInit, DoCheck } from '@angular/core';
import { UserService } from './services/user.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
  providers: [UserService]
})
export class AppComponent implements OnInit, DoCheck{


  title = 'aplicacion';

  public identity:any
  public token:any

  constructor(
    private _userService: UserService
  ){

    this.identity = this._userService.getIdentity();
    this.token = this._userService.getToken();

    console.log(this.identity);

  }




  ngOnInit(){

    console.log("app.compoenente cargado ");

  }

  ngDoCheck(): void {

    this.identity = this._userService.getIdentity();
    this.token = this._userService.getToken();

  }




}
<header id="header">

  <div class="navbar navbar-default" style="background: #15c3ff">

    <div class="navbar-header">
      <a class="navbar-brand">
        Mejora & soluciones
      </a>
    </div>

    <div class="collapse navbar-collapse" id="menu">

      <ul class="nav navbar-nav">
        <li>
          <a [routerLink]="['/']">Lista de productos</a>
        </li>
        <li *ngIf="identity.name">
          <a [routerLink]="['crear-product']">Crear producto</a>
        </li>
      </ul>

      <ul class="nav navbar-nav pull-right" *ngIf="!identity.name">
        <li>
          <a [routerLink]="['login']">Iniciar sesión</a>
        </li>
        <li *ngIf="identity">
          <a [routerLink]="['registro']">Registro</a>
        </li>
      </ul>

      <ul class="nav navbar-nav pull-right" *ngIf="identity.name">
        <li>
          <a href="#">{{ identity.name + ' ' + identity.surname }}</a>
        </li>
        <li>
          <a [routerLink]="['logout', 1]">
            <span class="glyphicon glyphicon-log-out"></span>
            Cerrar sesion
          </a>
        </li>
      </ul>

    </div>

   </div>

</header>

<router-outlet></router-outlet>
