/**
 *
 * @autor Christian Felipe Martinez Castaño
 * 2023
 *
 */
 import { Component, OnInit } from "@angular/core";
 import { Router, ActivatedRoute, Params } from "@angular/router";
 import { UserService } from "src/app/services/user.service";
 import { ProductsService } from "src/app/services/products.services";
 import { Products } from "src/app/models/products";

@Component({
  selector: 'app-products-edit',
  //templateUrl: './products-edit.component.html',
  templateUrl: '../products-new/products-new.component.html',
  styleUrls: ['./products-edit.component.scss'],
  providers: [UserService, ProductsService]
})
export class ProductsEditComponent {

  public page_title: string;
  public token: any;
  public products: Products;
  public status_products:any;

  constructor(
    private _route: ActivatedRoute,
    private _router: Router,
    private _userService: UserService,
    private _productsService: ProductsService
  ){
    this.page_title = 'Editar producto';
    this.token = this._userService.getToken();
    this.products = new Products(1, '','', 1, '', null, null);
  }


  ngOnInit(): void {
    this._route.params.subscribe(params => {
      let id = + params['id'];
      this.getProduct(id);
   })
  }


  getProduct(id:any){

      this._productsService.getProduct(this.token, id).subscribe(

        response =>{

          if(response.status == 'success'){
            this.products = response.products;
            this.page_title = "Editar producto " + this.products.title;
          }else{
           // this._router.navigate(['inicio']);
          }

        },
        error => {
          console.log(<any>error);
        }
       )


  }

  onSubmit(form: any){

    this._productsService.update(this.token, this.products, this.products.id).subscribe(

     response =>{

      console.log(response);

       if(response.status == 'success'){

        // this.products = response.product;
         //this.status_products = response.status;

         this._router.navigate(['inicio']);

       }else{
         this.status_products = 'error';
       }

     },
     error => {
       console.log(<any>error);

       this.status_products = 'error';

     }

    )


    console.log("submit del editarrr");

   }



}

<div class="col-md-10">

  <h2>{{page_title}}</h2>

  <div class="alert alert-success" *ngIf="status_products == 'success'">
    El producto se ha guardado correctamente.
  </div>
  <div class="alert alert-danger" *ngIf="status_products == 'error'">
    El producto no se ha guardado correctamente, intentalo de nuevo.
  </div>

  <form action="" #productsNew="ngForm" (ngSubmit)="onSubmit(productsNew)" class="col-md-5">

    <p>
      <label for="title">Titulo</label>
      <input type="text" class="form-control" name="title" #title="ngModel" [(ngModel)]="products.title" required>
      <span *ngIf="!title.valid && title.touched">El titulo es obligatorio</span>
    </p>

    <p>
      <label for="description">Descripcion</label>
      <input type="text" class="form-control" name="description" #description="ngModel" [(ngModel)]="products.description" required>
      <span *ngIf="!description.valid && description.touched">La descripcion es obligatoria</span>
    </p>

    <p>
      <label for="price">Precio</label>
      <input type="number" class="form-control" name="price" #price="ngModel" [(ngModel)]="products.price" required>
      <span *ngIf="!price.valid && price.touched">El precio es obligatorio</span>
    </p>

    <p>
      <label for="status">A la venta?</label>
     <select class="form-control" name="status" #status="ngModel" [(ngModel)]="products.status" required="">

      <option value="true">Si</option>
      <option value="false">No</option>

     </select>
      <span *ngIf="!status.valid && status.touched">EL estado del producto es obligatorio</span>
    </p>

    <p>
      <label for="user">Usuario</label>


<!--
      <select class="form-control" name="user"  >



      <option *ngFor="let prod of user" value="prod.id">
        {{prod.id}}
      </option>

     </select> -->


     <select name="status" placeholder="select" (ngModelChange)="onOptionsSelected()">
      <option *ngFor="let catalog of catalogs" [ngValue]="catalog">{{catalog.catalogname + "(" + catalog.catalogcode + ")"}}</option>
  </select>


     </p>

    <input type="submit" value="Registrar producto" class="btn btn-success" [disabled]="!productsNew.valid">

  </form>

</div>
