/**
 *
 * @autor Christian Felipe Martinez Castaño
 * 2023
 *
 */
import { Component, OnInit } from "@angular/core";
import { Router, ActivatedRoute, Params } from "@angular/router";
import { User } from "src/app/models/user";
import { UserService } from "src/app/services/user.service";

@Component({

  selector: 'register',
  templateUrl: 'register.component.html',
  providers: [UserService]

})

export class RegisterComponent implements OnInit{

  public title: string;
  public user:User;
  public status: string = '';

  constructor(
    private _route: ActivatedRoute,
    private _router: Router,
    private _userService: UserService
  ){

    this.title = 'Registrate';
    this.user = new User(1, 'ROLE_USER', '', '', '', '');

  }

  ngOnInit(): void {
    console.log("Register cargadoooo cabronnnn");
  }

  onSubmit(){

    this._userService.register(this.user).subscribe(

      response => {
        this.status = response.status;
        if(response.status == 'success'){

          //vaciar el formulario - objeto
          this.user = new User(1, 'ROLE_USER', '', '', '', '');

        }else{

        }
      },
      error => {
        console.log(<any>error);     }
    )

  }

}



  <div class="col-md-10">

    <h2>{{title}}</h2>

    <div class="alert alert-success" *ngIf="status == 'success'">

      El registros se ha completado correctamente, <a [routerLink]="['/login']">Identificate aqui</a>

    </div>

    <div class="alert alert-danger" *ngIf="status == 'error'">

      El registros no se ha completado correctamente

    </div>


    <form action="" #registerForm="ngForm" (ngSubmit)="onSubmit()" class="col-md-5">

      <p>
        <label for="name">Nombre</label>
        <input type="text" class="form-control" name="name" #name="ngModel" [(ngModel)]="user.name" required>
        <span *ngIf="!name.valid && name.touched">El nombre es obligatorio</span>
      </p>


      <p>
        <label for="surname">Apellidos</label>
        <input type="text" class="form-control" name="surname" #surname="ngModel" [(ngModel)]="user.surname" required>
        <span *ngIf="!surname.valid && surname.touched">Los apellidos es obligatorio</span>
      </p>

      <p>
        <label for="email">Email</label>
        <input type="text" class="form-control" name="email" #email="ngModel" [(ngModel)]="user.email" required>
        <span *ngIf="!email.valid && email.touched">El email es obligatorio</span>
      </p>

      <p>
        <label for="password">Password</label>
        <input type="password" class="form-control" name="password" #password="ngModel" [(ngModel)]="user.password" required>
        <span *ngIf="!password.valid && password.touched">El password es obligatorio</span>
      </p>

      <input type="submit" value="Registrarme" class="btn btn-success">


    </form>






  </div>
