import { Injectable } from "@angular/core";
import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Observable } from "rxjs";
import { GLOBAL } from "./global";
import { Car } from "../models/car";


@Injectable()

export class CarService {
  public url: string;

  constructor(
    public _http: HttpClient
  ){
    this.url = GLOBAL.url
  }


  pruebas(){
    return 'Hola mundo desde CArrrr';
  }

  create(token:any, car: any):Observable<any>{

    let jsonCar = JSON.stringify(car);

    let params = 'json='+jsonCar;

    let headers = new HttpHeaders().set('Content-Type', 'application/x-www-form-urlencoded')
                                   .set('Authorization', token);

    return this._http.post(this.url+'cars', params, {headers: headers});

  }



  getCars():Observable<any>{

    let headers = new HttpHeaders().set('Content-Type', 'application/x-www-form-urlencoded');

    return this._http.get(this.url+'cars',  {headers: headers});

  }




}
