import { Component, OnInit } from "@angular/core";
import { Router, ActivatedRoute, Params } from "@angular/router";
import { User } from "src/app/models/user";

import { UserService } from "src/app/services/user.service";

import { ProductsService } from "src/app/services/products.services";

import { Products } from "src/app/models/products";

@Component({

  selector: 'default',
  templateUrl: 'default.component.html',
  providers: [UserService, ProductsService]

})

export class DefaultComponent implements OnInit{

  public title: string;
  public token: any;
  public products: Array<Products>;


  constructor(
    private _route: ActivatedRoute,
    private _router: Router,
    private _userService: UserService,
    private _productsService: ProductsService
  ){

    this.title = 'Lista de productos';
    this.token = this._userService.getToken();
    this.products = [];
  }


  ngOnInit(): void {



    this._productsService.getProducts(this.token).subscribe(

      response =>{

       // console.log(response.status);

        if(response.status == 'success'){

          this.products = response.products;

          console.log(this.products);

          this._router.navigate(['inicio']);

        }else{

        }



      },
      error => {

        console.log(<any>error);

      }

     )










  }



}
<div class="loading" *ngIf="!products">
  Cargando...

</div>

<div class="col-md-12" *ngIf="products">
  <h2>{{title}}</h2>
<br />
  <table class="table table-hover table-stripped" id="products-list">

    <tr>
      <th class="col-md-7">
        Producto
      </th>
      <th>
        Precio
      </th>
      <th>
        Disponible
      </th>
      <th>
        Acciones
      </th>
    </tr>
    <tr *ngFor="let prod of products">
      <td class="col-md-7">
       {{prod.title}}
      </td>
      <td>
        {{prod.price}}
      </td>
      <td>
        <span *ngIf="prod.status == 'true'">Si</span>
        <span *ngIf="prod.status != 'true'">No</span>

      </td>
      <td>
        <a [routerLink]="['editar-product', prod.id]" class="btn btn-warning">Editar</a>
        <a href="#" class="btn btn-danger">Borrar</a>
      </td>
    </tr>

  </table>




<table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td>Larry</td>
      <td>the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>




</div>
